export default function FilterForm(props) {
    // fetchUrl search_movies
    // const {fetchUrl, defaultMovies} = props;

    const [title, setTitle] = useState('');
    const [selectedGenre, setSelectedGenre] = useState('');
    const [selectedLanguage, setSelectedLanguage] = useState('');
    const [selectedCountry, setSelectedCountry] = useState('');
    const [checkIsOscar, setCheckIsOscar] = useState(true);
    const [yearRange, setYearRange] = useState([1900, 2023]);

    // useEffect(() => {
    //     await fetch(`http://${config.server_host}:${config.server_port}/search_movies?title=${title}&genre=${selectedGenre}&language=${selectedLanguage}&country=${selectedCountry}&year_low=${yearRange[0]}&year_low=${yearRange[1]}&isOscar=${checkIsOscar}`);
    //       .then(res => res.json())
    //       .then(resJson => {
    //         setResults(resJson);
    //         setCurrentPage(1);
    //       });
    //   }, [activeTab]);
    // useEffect(() => {
    //     fetch(`http://${config.server_host}:${config.server_port}/${fetchUrl}/${activeTab}`)
    //       .then(res => res.json())
    //       .then(resJson => {
    //         setResults(resJson);
    //         setCurrentPage(1);
    //       });
    //   }, [activeTab]);

    const handleTitleChange = (event) => {
        setTitle(event.target.value);
    };
    const handleGenreChange = (event) => {
        setSelectedGenre(event.target.value);
    };
    const handleLanguageChange = (event) => {
        setSelectedLanguage(event.target.value);
    };
    const handleCountryChange = (event) => {
        setSelectedCountry(event.target.value);
    };
    const handleOscarChange = (event) => {
        setCheckIsOscar(event.target.checked);
    };
    const handleYearRangeChange = (event, newValue) => {
        setYearRange(newValue);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const queryParams = new URLSearchParams({
            title: title,
            genre: selectedGenre,
            language: selectedLanguage,
            country: selectedCountry,
            isOscar: checkIsOscar,
            year_start: yearRange[0],
            year_end: yearRange[1]
        });
        const url = `http://${config.server_host}:${config.server_port}/search_movies?title=${title}&genre=${selectedGenre}&language=${selectedLanguage}&country=${selectedCountry}&year_low=${yearRange[0]}&year_low=${yearRange[1]}&isOscar=${checkIsOscar}`;
        fetch(url)
            .then(response => response.json())
            .then(data => {
                // set filtered movies to the data received from the server
                setFilteredMovies(data);
            })
            .catch(error => console.error(error));
        props.onFilter(filteredMovies);
    };

    return (
        <Container>
            <Box
                component="form"
                sx={{
                    '& > :not(style)': { m: 1, minwidth: '25ch' },
                    display: 'flex',
                    flexWrap: 'wrap',
                    justifyContent: 'center',
                    alignItems: 'center',
                }}
                noValidate
                autoComplete="off"
                onSubmit={handleSubmit}

            >
                <TextField
                    id="movieTitle"
                    label="Movie Title"
                    value={title}
                    onChange={handleTitleChange}
                    helperText="Please input the title"
                />
                <TextField
                    id="genreSelection"
                    select
                    label="Genre"
                    value={selectedGenre}
                    onChange={handleGenreChange}
                    defaultValue=""
                    helperText="Please select the genre"
                >
                    {genres.map((option) => (
                        <MenuItem key={option} value={option}>
                            {option}
                        </MenuItem>
                    ))}
                </TextField>
                <TextField
                    id="languageSelection"
                    select
                    label="Language"
                    value={selectedLanguage}
                    onChange={handleLanguageChange}
                    defaultValue=""
                    helperText="Please select the language"
                >
                    {languages.map((option) => (
                        <MenuItem key={option} value={option}>
                            {option}
                        </MenuItem>
                    ))}
                </TextField>
                <TextField
                    id="countrySelection"
                    select
                    label="Country"
                    value={selectedCountry}
                    onChange={handleCountryChange}
                    defaultValue=""
                    helperText="Please select the country"
                >
                    {countries.map((option) => (
                        <MenuItem key={option} value={option}>
                            {option}
                        </MenuItem>
                    ))}
                </TextField>
                <FormControlLabel
                    control={
                        <Checkbox
                            checked={checkIsOscar}
                            onChange={handleOscarChange}
                            name="isOscar"
                            color="primary"
                        />
                    }
                    label="isOscar"
                />
                <Slider
                    value={yearRange}
                    onChange={handleYearRangeChange}
                    valueLabelDisplay="auto"
                    aria-labelledby="year-range-slider"
                    min={1920}
                    max={2023}
                    step={1}
                    style={{ width: 200 }}
                    helperText="Please select the year range"
                />
                <Button sx={{ alignSelf: 'flex-start', height: '55px', width: '30px' }}
                    variant="outlined"
                    size="small"
                // sx={{ height: '50px', width: '20px' }}
                >
                    Find
                </Button>

            </Box>
        </Container >
    );
}
