import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Link, Stack, Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from '@mui/material';
import { createTheme, responsiveFontSizes } from '@mui/material/styles';

import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import Rating from '@mui/material/Rating';


const config = require('../config.json');

const styles = {
  cardContainer: {
    display: 'flex',
    flexDirection: 'row',
    alignItems: 'center',
    width: '90%',
    margin: ' auto',
  },
  cardMedia: {
    width: 400, // set a fixed width
    height: 500, // set a fixed height
    flex: '0 0 200px',
  },
  cardContent: {
    flex: '1 1 auto',
    margin: '0 16px',
    minWidth: 0,
    // fontSize: '2rem',
  },
};

export default function MovieInfoPage() {
  const { movie_id } = useParams();

  // const [songData, setSongData] = useState([{}]); // default should actually just be [], but empty object element added to avoid error in template code
  const [movieData, setMovieData] = useState([]);
  const [moviePeople, setMoviePeople] = useState([{}]);

  useEffect(() => {
    fetch(`http://${config.server_host}:${config.server_port}/movies/${movie_id}`)
      .then(res => res.json())
      .then(resJson => {
        console.log('Movie data:', resJson);
        setMovieData(resJson);
      });

    fetch(`http://${config.server_host}:${config.server_port}/movie_people/${movie_id}`)
      .then(res => res.json())
      .then(resJson => setMoviePeople(resJson));
  }, [movie_id]);

  return (
    <Container>
      <div style={{ margin: '5%' }}>
        <Card sx={styles.cardContainer}>
          <CardMedia
            component="img"
            sx={styles.cardMedia}
            image={movieData.poster_url}
          />
          <CardContent sx={styles.cardContent}>
            <Typography gutterBottom variant="h4" component="div" >
              {movieData.title}
            </Typography>
            <Typography component="legend" gutterBottom variant="h5">Rating:</Typography>
            <Rating name="read-only" value={movieData.avg_vote} readOnly />
            <Typography variant="body1" color="text.primary" sx={{ fontSize: 20 }}>
              Release year: {movieData.year}
            </Typography>
            <Typography variant="body1" color="text.primary" sx={{ fontSize: 20 }}>
              Duration: {movieData.duration} minutes
            </Typography>
            <Typography variant="body1" color="text.primary" sx={{ fontSize: 20 }}>
              Director: {movieData.director}
            </Typography>
            <Typography variant="body1" color="text.primary" sx={{ fontSize: 20 }}>
              Oscar Nominations: {movieData.Oscar_nominated}
            </Typography>
            <Typography variant="body1" color="text.secondary" style={{ marginTop: '18px' }}>
              {movieData.description}
            </Typography>
          </CardContent>
          {/* <CardActions>
        <Button size="small">Share</Button>
        <Button size="small">Learn More</Button>
      </CardActions> */}
        </Card>
      </div>
    </Container>
  );
}
