import { useState } from 'react';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import { Container } from '@mui/material';
import MenuItem from '@mui/material/MenuItem';
import Select from '@mui/material/Select';

const genres = ['Action', 'Adventure', 'Animation', 'Biography', 'Comedy', 'Crime', 'Documentary', 'Drama', 'Family', 'Fantasy', 'Film-Noir', 'History', 'Horror', 'Music', 'Musical', 'Mystery', 'Reality-TV', 'Romance', 'Sci-Fi', 'Sport', 'Thriller', 'War', 'Western'];


export default function FilterForm() {
    const [title, setTitle] = useState('');
    const [selectedGenre, setSelectedGenre] = useState('');

    const handleTitleChange = (event) => {
        setTitle(event.target.value);
    };
    const handleGenreChange = (event) => {
        setSelectedGenre(event.target.value);
    };

    return (
        <Container>
            <Box
                component="form"
                sx={{
                    '& > :not(style)': { m: 1, width: '25ch' },
                }}
                noValidate
                autoComplete="off"
            >
                <TextField
                    id="movieTitle"
                    label="Movie Title"
                    value={title}
                    onChange={handleTitleChange}
                />
                <TextField
                    id="genreSelection"
                    select
                    label="Genre"
                    value={selectedGenre}
                    onChange={handleGenreChange}
                    defaultValue=""
                    helperText="Please select the genre"
                >
                    {genres.map((option) => (
                        <MenuItem key={option} value={option}>
                            {option}
                        </MenuItem>
                    ))}
                </TextField>

            </Box>
        </Container>


    );
}
